import {useState} from "react"

const Statistics =(props) => {

  if (props.all === 0 ){
    return (
      <div>
        <h1>statistics</h1>
        <p>No feedback given</p>
        </div>
    )
  }
  return (
    <div>
    <h1>statistics</h1>
    <li>good {props.good}</li>
    <li>neural {props.neutral}</li>
    <li>bad {props.bad}</li>
    <li>all {props.all}</li>
    <li>average {props.average}</li>
    <li>positive {props.positive}</li>
    </div>
  )
}




const App = () =>{
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  // caculate the total number of collected feedback
  const all = good + neutral + bad

  // caculate the average score of the feedback
  const average = (good - bad) / all

  //caculate the percentage of positive feedback
  const positive = good / all

  return(
    <div>
      <h1>give feedback</h1>
      <button onClick={()=>{setGood(good + 1)}}>good</button>
      <button onClick={()=>{setNeutral(neutral + 1)}}>neutral</button>
      <button onClick={()=>{setBad(bad + 1)}}>bad</button>
      <Statistics good={good} neutral={neutral} bad={bad} all={all} average={average} positive={positive}/>
    </div>
  )
}

export default App;
